import React, { useState } from "react";
import { TextField, Button, Box, Typography, CircularProgress, Paper } from "@mui/material";

function App() {
  const [message, setMessage] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const handleSendMessage = async () => {
    if (!message) return;

    setChatHistory((prev) => [...prev, { role: "user", content: message }]);
    setIsLoading(true);

    try {
      const response = await fetch("http://localhost:5000/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message }),
      });

      const data = await response.json();

      setChatHistory((prev) => [...prev, { role: "assistant", content: data.reply }]);
    } catch (error) {
      console.error("Error communicating with backend:", error);
      setChatHistory((prev) => [
        ...prev,
        { role: "assistant", content: "Error: Unable to fetch response from backend." },
      ]);
    } finally {
      setIsLoading(false);
      setMessage("");
    }
  };

  return (
    <Box
      sx={{
        display: "flex",
        flexDirection: "column",
        height: "100vh",
        padding: "16px",
        fontFamily: "Arial, sans-serif",
      }}
    >
      <Typography variant="h3" component="h1" gutterBottom sx={{ textAlign: "center" }}>
        ChatDDK
      </Typography>

      <Paper
        elevation={3}
        sx={{
          flex: 1,
          overflowY: "auto",
          padding: "16px",
          backgroundColor: "#f9f9f9",
        }}
      >
        {chatHistory.map((entry, index) => (
          <Box
            key={index}
            sx={{
              display: "flex",
              justifyContent: entry.role === "user" ? "flex-end" : "flex-start",
              mb: 2,
            }}
          >
            <Typography
              sx={{
                padding: "10px",
                borderRadius: "8px",
                backgroundColor: entry.role === "user" ? "#1976d2" : "#e0e0e0",
                color: entry.role === "user" ? "#fff" : "#000",
                maxWidth: "60%",
              }}
            >
              {entry.content}
            </Typography>
          </Box>
        ))}
        {isLoading && (
          <Box sx={{ display: "flex", justifyContent: "center", mt: 2 }}>
            <CircularProgress />
          </Box>
        )}
      </Paper>

      <Box sx={{ display: "flex", alignItems: "center", mt: 2 }}>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Type your message..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && handleSendMessage()}
        />
        <Button
          variant="contained"
          color="primary"
          onClick={handleSendMessage}
          sx={{ ml: 2, padding: "10px 20px" }}
        >
          Send
        </Button>
      </Box>
    </Box>
  );
}

export default App;
