import React, { useState } from "react";
import {
  TextField,
  Button,
  Box,
  Typography,
  CircularProgress,
  Paper,
} from "@mui/material";
import "./index.css"; // Custom styles

function App() {
  const [message, setMessage] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  // Function to send a message
  const handleSendMessage = async () => {
    if (!message.trim()) return;

    setChatHistory((prev) => [...prev, { role: "user", content: message }]);
    setIsLoading(true);

    try {
      const response = await fetch("http://localhost:5000/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message }),
      });

      const data = await response.json();

      setChatHistory((prev) => [
        ...prev,
        { role: "assistant", content: data.reply },
      ]);
    } catch (error) {
      console.error("Error communicating with backend:", error);
      setChatHistory((prev) => [
        ...prev,
        {
          role: "assistant",
          content: "Error: Unable to fetch response from ChatDDK backend.",
        },
      ]);
    } finally {
      setIsLoading(false);
      setMessage("");
    }
  };

  // Function to clear the chat
  const handleClearChat = async () => {
    try {
      const response = await fetch("http://localhost:5000/reset", {
        method: "POST",
      });

      if (response.ok) {
        setChatHistory([]);
        console.log("Conversation reset successfully.");
      } else {
        console.error("Failed to reset conversation.");
      }
    } catch (error) {
      console.error("Error resetting conversation:", error);
    }
  };

  return (
    <Box sx={{ padding: "16px", fontFamily: "Arial, sans-serif" }}>
      <Typography variant="h3" gutterBottom>
        ChatDDK
      </Typography>

      <Paper elevation={3} className="chat-container">
        {chatHistory.map((entry, index) => (
          <Box
            key={index}
            className={
              entry.role === "user"
                ? "user-message-container"
                : "assistant-message-container"
            }
          >
            <Typography
              className={
                entry.role === "user" ? "user-message" : "assistant-message"
              }
            >
              {entry.content}
            </Typography>
          </Box>
        ))}
        {isLoading && (
          <Box sx={{ textAlign: "center", marginTop: "10px" }}>
            <CircularProgress size={24} />
            <Typography variant="body2">Thinking...</Typography>
          </Box>
        )}
      </Paper>

      <Box sx={{ display: "flex", marginTop: "10px" }}>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Type your message..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && handleSendMessage()}
        />
        <Button
          variant="contained"
          color="primary"
          onClick={handleSendMessage}
          sx={{ ml: 2, padding: "10px 20px" }}
        >
          Send
        </Button>
        <Button
          variant="outlined"
          color="secondary"
          onClick={handleClearChat}
          sx={{ marginLeft: "10px" }}
        >
          Clear Chat
        </Button>
      </Box>
    </Box>
  );
}

export default App;
